import React, { useState, useEffect } from "react";
import PartyForm from "../components/PartyForm";
import partyService from "../services/partyService";
import { UserCircle, Phone, MapPin, BadgePercent } from "lucide-react";

const PartiesPage = () => {
  const [parties, setParties] = useState([]);

  const fetchParties = async () => {
    try {
      const data = await partyService.getAllParties(); // <-- Show all parties
      setParties(data);
    } catch (err) {
      console.error("Failed to fetch parties", err);
    }
  };

  useEffect(() => {
    fetchParties();
  }, []);

  return (
    <div className="p-6 bg-gray-50 min-h-screen">
      <h1 className="text-3xl font-bold mb-6 text-blue-800">All Parties</h1>

      <div className="grid grid-cols-1 lg:grid-cols-3 gap-6">
        {/* Add Party Form */}
        <div className="lg:col-span-1 bg-white rounded-xl shadow-md p-4">
          <h2 className="text-xl font-semibold mb-4 text-gray-700">âž• Add New Party</h2>
          <PartyForm onAdded={fetchParties} />
        </div>

        {/* Party List */}
        <div className="lg:col-span-2">
          {parties.length === 0 ? (
            <p className="text-gray-600">No parties available.</p>
          ) : (
            <div className="grid md:grid-cols-2 gap-4">
              {parties.map((party) => (
                <div
                  key={party.id}
                  className="bg-white rounded-xl shadow-sm p-5 hover:shadow-md transition-shadow duration-200"
                >
                  <h3 className="text-lg font-bold text-blue-700 flex items-center gap-2 mb-2">
                    <UserCircle size={20} />
                    {party.name}
                  </h3>
                  <div className="text-gray-700 space-y-1 text-sm">
                    <div className="flex items-center gap-2">
                      <Phone size={16} className="text-gray-500" />
                      <span>{party.contact || "Not Provided"}</span>
                    </div>
                    <div className="flex items-center gap-2">
                      <MapPin size={16} className="text-gray-500" />
                      <span>{party.address || "No address"}</span>
                    </div>
                    <div className="flex items-center gap-2">
                      <BadgePercent size={16} className="text-gray-500" />
                      <span>{party.gstin || "GSTIN not available"}</span>
                    </div>
                  </div>
                </div>
              ))}
            </div>
          )}
        </div>
      </div>
    </div>
  );
};

export default PartiesPage;
