import React, { useState, useEffect } from "react";
import PartyForm from "../components/PartyForm";
import partyService from "../services/partyService";

const PartiesPage = () => {
  const [parties, setParties] = useState([]);

  const fetchParties = async () => {
    try {
      const data = await partyService.getAllParties();
      setParties(data);
    } catch (err) {
      console.error("Failed to fetch parties", err);
    }
  };

  useEffect(() => {
    fetchParties();
  }, []);

  return (
    <div className="p-6 max-w-6xl mx-auto space-y-6">
      <h1 className="text-3xl font-bold text-gray-800">Parties</h1>

      <div className="bg-white p-6 rounded-2xl shadow-md">
        <PartyForm onAdded={fetchParties} />
      </div>

      <div className="bg-white rounded-2xl shadow-md overflow-hidden">
        <table className="w-full">
          <thead className="bg-gray-50">
            <tr>
              <th className="px-4 py-3 text-left text-sm font-medium text-gray-600">Name</th>
              <th className="px-4 py-3 text-left text-sm font-medium text-gray-600">Contact</th>
              <th className="px-4 py-3 text-left text-sm font-medium text-gray-600">Address</th>
              <th className="px-4 py-3 text-left text-sm font-medium text-gray-600">GSTIN</th>
            </tr>
          </thead>
          <tbody>
            {parties.length > 0 ? (
              parties.map((party) => (
                <tr key={party.id} className="hover:bg-gray-50">
                  <td className="px-4 py-2 border-t">{party.name}</td>
                  <td className="px-4 py-2 border-t">{party.contact}</td>
                  <td className="px-4 py-2 border-t">{party.address}</td>
                  <td className="px-4 py-2 border-t">{party.gstin}</td>
                </tr>
              ))
            ) : (
              <tr>
                <td colSpan={4} className="text-center py-6 text-gray-500">
                  No parties found.
                </td>
              </tr>
            )}
          </tbody>
        </table>
      </div>
    </div>
  );
};

export default PartiesPage;
