import React from "react";

function TransactionTable({ transactions }) {
  if (transactions === undefined) {
    return <p className="text-gray-500">Loading transactions...</p>;
  }

  if (!Array.isArray(transactions) || transactions.length === 0) {
    return <p className="text-gray-500">No transactions available.</p>;
  }

  return (
    <div className="overflow-x-auto">
      <table className="min-w-full table-auto border border-gray-300 rounded">
        <thead className="bg-gray-100 sticky top-0 z-10">
          <tr>
            <th className="px-4 py-2 border">Date</th>
            <th className="px-4 py-2 border">Party</th>
            <th className="px-4 py-2 border">Factory</th>
            <th className="px-4 py-2 border">Weight (kg)</th>
            <th className="px-4 py-2 border">Rate (₹)</th>
            <th className="px-4 py-2 border">Amount (₹)</th>
          </tr>
        </thead>
        <tbody>
          {transactions.map((txn) => (
            <tr key={txn.id || `${txn.date}-${txn.party_name}-${txn.factory_name}`} className="text-center border-t hover:bg-gray-50">
              <td className="px-4 py-2 border">{txn.date || "-"}</td>
              <td className="px-4 py-2 border">{txn.party_name || "-"}</td>
              <td className="px-4 py-2 border">{txn.factory_name || "-"}</td>
              <td className="px-4 py-2 border">{txn.weight || "-"}</td>
              <td className="px-4 py-2 border">{txn.rate || "-"}</td>
              <td className="px-4 py-2 border">
                {
                  new Intl.NumberFormat("en-IN", {
                    style: "currency",
                    currency: "INR",
                  }).format(txn.amount || 0)
                }
              </td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
}

export default TransactionTable;
