import { NavLink } from 'react-router-dom';
import { Home, Users, Factory, FileText } from 'lucide-react';

const links = [
  { to: '/', label: 'Dashboard', icon: <Home size={20} /> },
  { to: '/parties', label: 'Parties', icon: <Users size={20} /> },
  { to: '/factories', label: 'Factories', icon: <Factory size={20} /> },
  { to: '/transactions', label: 'Transactions', icon: <FileText size={20} /> },
];

const Sidebar = () => {
  return (
    <aside className="bg-[#111827] text-white w-64 min-h-screen shadow-xl p-4 sticky top-0 flex flex-col">
      {/* Brand / Logo */}
      <div className="flex items-center justify-center mb-8">
        <div className="bg-[#34D399] p-3 rounded-full shadow-lg">
          <Factory size={28} className="text-gray-900" />
        </div>
        <h1 className="ml-3 text-2xl font-bold tracking-wide text-[#34D399]">
          Scrap Manager
        </h1>
      </div>

      {/* Navigation */}
      <nav className="flex flex-col space-y-1">
        {links.map(({ to, label, icon }) => (
          <NavLink
            key={to}
            to={to}
            className={({ isActive }) =>
              `group flex items-center gap-4 px-4 py-3 rounded-lg text-sm font-medium transition-all duration-200
              ${isActive ? 'bg-[#34D399]/20 text-[#34D399] shadow-inner' : 'hover:bg-[#1F2937] hover:text-[#34D399]'}`
            }
          >
            <div className="relative">
              {icon}
              {/* Dot indicator for active state */}
              <span className={`absolute -left-3 top-1/2 transform -translate-y-1/2 w-2 h-2 rounded-full bg-[#34D399] transition-opacity duration-300 ${isActive ? 'opacity-100' : 'opacity-0'}`} />
            </div>
            <span className="transition-colors">{label}</span>
          </NavLink>
        ))}
      </nav>

      {/* Optional footer or user area */}
      <div className="mt-auto pt-6 text-center text-xs text-gray-400">
        Â© 2025 Scrap Manager
      </div>
    </aside>
  );
};

export default Sidebar;
