import React from "react";

const SummarySection = ({ label, total, paidOrReceived, remaining }) => {
  return (
    <div className="grid grid-cols-1 md:grid-cols-3 gap-6 mb-6">
      <div className="p-4 bg-green-100 rounded">
        <h4 className="font-bold">Total Amount</h4>
        <p className="text-lg font-semibold">₹ {total?.toFixed(2)}</p>
      </div>
      <div className="p-4 bg-blue-100 rounded">
        <h4 className="font-bold">{label}</h4>
        <p className="text-lg font-semibold">₹ {paidOrReceived?.toFixed(2)}</p>
      </div>
      <div className="p-4 bg-yellow-100 rounded">
        <h4 className="font-bold">Remaining</h4>
        <p className="text-lg font-semibold">₹ {remaining?.toFixed(2)}</p>
      </div>
    </div>
  );
};

export default SummarySection;
