import React, { useEffect, useState } from 'react';
import axios from 'axios';
import jsPDF from 'jspdf';
import 'jspdf-autotable';
import * as XLSX from 'xlsx';
import { saveAs } from 'file-saver';
import { formatCurrency } from '../utils/formatter';
import TransactionTable from '../components/TransactionTable';
import HisabTally from '../pages/HisabTally';

const DashboardPage = () => {
  const [partyId, setPartyId] = useState('');
  const [factoryId, setFactoryId] = useState('');
  const [partyOptions, setPartyOptions] = useState([]);
  const [factoryOptions, setFactoryOptions] = useState([]);
  // const [partyData, setPartyData] = useState(null);
  // const [factoryData, setFactoryData] = useState(null);

  useEffect(() => {
    const fetchCounts = async () => {
      try {
        const [partiesRes, factoriesRes] = await Promise.all([
          axios.get('http://localhost:5001/api/parties'),
          axios.get('http://localhost:5001/api/factories'),
        ]);
        setPartyOptions(partiesRes.data);
        setFactoryOptions(factoriesRes.data);
      } catch (err) {
        console.error('Error fetching counts', err);
      }
    };

    fetchCounts();
  }, []);

  // useEffect(() => {
  //   if (partyId) {
  //     axios
  //       .get(`http://localhost:5001/api/dashboard/party/${partyId}`)
  //       .then((res) => setPartyData(res.data))
  //       .catch((err) => console.error(err));
  //   } else {
  //     setPartyData(null);
  //   }

  //   if (factoryId) {
  //     axios
  //       .get(`http://localhost:5001/api/dashboard/factory/${factoryId}`)
  //       .then((res) => setFactoryData(res.data))
  //       .catch((err) => console.error(err));
  //   } else {
  //     setFactoryData(null);
  //   }
  // }, [partyId, factoryId]);

  const exportSummaryAsPDF = (type) => {
    const data = type === 'party' ? partyData : factoryData;
    if (!data) return;

    const doc = new jsPDF();
    doc.setFontSize(18);
    doc.text(`${type === 'party' ? 'Party' : 'Factory'} Summary`, 14, 22);
    doc.setFontSize(12);
    doc.text(`Total Amount: ₹ ${data.totalAmount}`, 14, 32);
    doc.text(
      `${type === 'party' ? 'Total Paid' : 'Total Received'}: ₹ ${
        type === 'party' ? data.totalPaid : data.totalReceived
      }`,
      14,
      40
    );
    doc.text(`Remaining: ₹ ${data.remaining}`, 14, 48);

    const columns =
      type === 'party'
        ? ['Date', 'Vehicle', 'Weight', 'Rate', 'Moisture', 'Rejection', 'Duplex', 'Total']
        : ['Date', 'Vehicle', 'Weight', 'Rate', 'Total'];

    const rows = data.transactions.map((tx) =>
      type === 'party'
        ? [
            tx.date,
            tx.vehicle_no || '',
            tx.weight,
            tx.rate,
            tx.moisture || '-',
            tx.rejection || '-',
            tx.duplex || '-',
            `₹ ${tx.total_amount}`,
          ]
        : [tx.date, tx.vehicle_no || '', tx.weight, tx.rate, `₹ ${tx.total_amount}`]
    );

    doc.autoTable({
      startY: 60,
      head: [columns],
      body: rows,
      styles: { fontSize: 9 },
    });

    doc.save(`${type}_summary_${Date.now()}.pdf`);
  };

  const exportSummaryAsExcel = (type) => {
    const data = type === 'party' ? partyData : factoryData;
    if (!data) return;

    const summarySheet = [
      ['Total Amount', data.totalAmount],
      [type === 'party' ? 'Total Paid' : 'Total Received', type === 'party' ? data.totalPaid : data.totalReceived],
      ['Remaining', data.remaining],
    ];

    const transactionHeaders =
      type === 'party'
        ? ['Date', 'Vehicle No', 'Weight', 'Rate', 'Moisture', 'Rejection', 'Duplex', 'Total Amount']
        : ['Date', 'Vehicle No', 'Weight', 'Rate', 'Total Amount'];

    const transactionRows = data.transactions.map((tx) =>
      type === 'party'
        ? [tx.date, tx.vehicle_no, tx.weight, tx.rate, tx.moisture, tx.rejection, tx.duplex, tx.total_amount]
        : [tx.date, tx.vehicle_no, tx.weight, tx.rate, tx.total_amount]
    );

    const wb = XLSX.utils.book_new();
    const wsSummary = XLSX.utils.aoa_to_sheet(summarySheet);
    const wsTransactions = XLSX.utils.aoa_to_sheet([transactionHeaders, ...transactionRows]);

    XLSX.utils.book_append_sheet(wb, wsSummary, 'Summary');
    XLSX.utils.book_append_sheet(wb, wsTransactions, 'Transactions');

    const wbout = XLSX.write(wb, { bookType: 'xlsx', type: 'array' });
    saveAs(new Blob([wbout], { type: 'application/octet-stream' }), `${type}_summary_${Date.now()}.xlsx`);
  };

  return (
    <div className="p-6">
      <h2 className="text-3xl font-bold mb-6">Dashboard</h2>

      <div className="grid grid-cols-1 md:grid-cols-2 gap-6 mb-8">
        <div className="bg-white shadow rounded p-5 text-center">
          <h3 className="text-xl font-semibold text-gray-700">Total Parties</h3>
          <p className="text-3xl font-bold text-blue-600 mt-2">{partyOptions.length}</p>
        </div>
        <div className="bg-white shadow rounded p-5 text-center">
          <h3 className="text-xl font-semibold text-gray-700">Total Factories</h3>
          <p className="text-3xl font-bold text-green-600 mt-2">{factoryOptions.length}</p>
        </div>
      </div>

      <div className="grid grid-cols-1 md:grid-cols-2 gap-6 mb-8">
        <div>
          <label className="block font-semibold mb-1">Select Party:</label>
          <select
            value={partyId}
            onChange={(e) => setPartyId(e.target.value)}
            className="w-full border border-gray-300 p-2 rounded"
          >
            <option value="">-- Select Party --</option>
            {partyOptions.map((p) => (
              <option key={p.id} value={p.id}>
                {p.name}
              </option>
            ))}
          </select>
        </div>

        <div>
          <label className="block font-semibold mb-1">Select Factory:</label>
          <select
            value={factoryId}
            onChange={(e) => setFactoryId(e.target.value)}
            className="w-full border border-gray-300 p-2 rounded"
          >
            <option value="">-- Select Factory --</option>
            {factoryOptions.map((f) => (
              <option key={f.id} value={f.id}>
                {f.name}
              </option>
            ))}
          </select>
        </div>
      </div>

      <HisabTally filters={{ partyId, factoryId }} />

      {partyData && (
        <div className="bg-white p-5 shadow rounded mb-8">
          <div className="flex justify-between items-center mb-4">
            <h3 className="text-xl font-semibold">Party Summary</h3>
            <div className="flex gap-2">
              <button
                onClick={() => exportSummaryAsPDF('party')}
                className="bg-blue-600 text-white px-3 py-1 rounded hover:bg-blue-700"
              >
                Export PDF
              </button>
              <button
                onClick={() => exportSummaryAsExcel('party')}
                className="bg-green-600 text-white px-3 py-1 rounded hover:bg-green-700"
              >
                Export Excel
              </button>
            </div>
          </div>
          <p>
            <strong>Total Amount:</strong> {formatCurrency(partyData.totalAmount)}
          </p>
          <p>
            <strong>Total Paid:</strong> {formatCurrency(partyData.totalPaid)}
          </p>
          <p>
            <strong>Remaining:</strong> {formatCurrency(partyData.remaining)}
          </p>
          <h4 className="mt-4 font-semibold">Transactions</h4>
          <TransactionTable transactions={partyData.transactions} type="party" />
        </div>
      )}

      {factoryData && (
        <div className="bg-white p-5 shadow rounded">
          <div className="flex justify-between items-center mb-4">
            <h3 className="text-xl font-semibold">Factory Summary</h3>
            <div className="flex gap-2">
              <button
                onClick={() => exportSummaryAsPDF('factory')}
                className="bg-blue-600 text-white px-3 py-1 rounded hover:bg-blue-700"
              >
                Export PDF
              </button>
              <button
                onClick={() => exportSummaryAsExcel('factory')}
                className="bg-green-600 text-white px-3 py-1 rounded hover:bg-green-700"
              >
                Export Excel
              </button>
            </div>
          </div>
          <p>
            <strong>Total Amount:</strong> {formatCurrency(factoryData.totalAmount)}
          </p>
          <p>
            <strong>Total Received:</strong> {formatCurrency(factoryData.totalReceived)}
          </p>
          <p>
            <strong>Remaining:</strong> {formatCurrency(factoryData.remaining)}
          </p>
          <h4 className="mt-4 font-semibold">Transactions</h4>
          <TransactionTable transactions={factoryData.transactions} type="factory" />
        </div>
      )}
    </div>
  );
};

export default DashboardPage;
