// Sidebar.jsx
import { NavLink } from 'react-router-dom';
import { Home, Users, Factory, FileText } from 'lucide-react';

const links = [
  { to: '/', label: 'Dashboard', icon: <Home size={20} /> },
  { to: '/parties', label: 'Parties', icon: <Users size={20} /> },
  { to: '/factories', label: 'Factories', icon: <Factory size={20} /> },
  { to: '/transactions', label: 'Transactions', icon: <FileText size={20} /> },
];

const Sidebar = () => {
  return (
    <div className="bg-[#1F2937] text-white w-60 min-h-screen shadow-xl p-4">
      <h1 className="text-2xl font-bold text-center mb-8 tracking-wide text-[#34D399]">
        Scrap Manager
      </h1>
      <nav className="space-y-2">
        {links.map(({ to, label, icon }) => (
          <NavLink
            key={to}
            to={to}
            className={({ isActive }) =>
              `flex items-center gap-3 px-4 py-2 rounded-lg text-sm font-medium transition-all
               ${isActive ? 'bg-[#34D399] text-gray-900' : 'hover:bg-[#374151]'}`
            }
          >
            {icon}
            {label}
          </NavLink>
        ))}
      </nav>
    </div>
  );
};

export default Sidebar;
