import React from "react";

const formatCurrency = (value) =>
  new Intl.NumberFormat("en-IN", {
    style: "currency",
    currency: "INR",
    minimumFractionDigits: 2,
  }).format(value || 0);

const SummarySection = ({ label, total, paidOrReceived, remaining }) => {
  return (
    <div className="grid grid-cols-1 md:grid-cols-3 gap-6 mb-6">
      <div className="p-4 bg-green-100 rounded-lg shadow-sm">
        <h4 className="font-bold text-gray-700">Total Amount</h4>
        <p className="text-xl font-semibold text-green-800">
          {formatCurrency(total)}
        </p>
      </div>
      <div className="p-4 bg-blue-100 rounded-lg shadow-sm">
        <h4 className="font-bold text-gray-700">{label}</h4>
        <p className="text-xl font-semibold text-blue-800">
          {formatCurrency(paidOrReceived)}
        </p>
      </div>
      <div className="p-4 bg-yellow-100 rounded-lg shadow-sm">
        <h4 className="font-bold text-gray-700">Remaining</h4>
        <p className="text-xl font-semibold text-yellow-800">
          {formatCurrency(remaining)}
        </p>
      </div>
    </div>
  );
};

export default SummarySection;
