import React from "react";
import { formatCurrency } from "../src/utils"; 

const SummaryCard = ({ title, value, color }) => (
  <div className={`p-5 rounded-xl shadow-md text-white ${color}`}>
    <h4 className="text-md font-medium">{title}</h4>
    <p className="text-xl font-bold mt-1">â‚¹ {value?.toFixed(2)}</p>
  </div>
);

const SummarySection = ({ label, total, paidOrReceived, remaining }) => {
  return (
    <div className="grid grid-cols-1 md:grid-cols-3 gap-6 mb-6">
      <SummaryCard title="Total Amount" value={total} color="bg-green-500" />
      <SummaryCard title={label} value={paidOrReceived} color="bg-blue-500" />
      <SummaryCard title="Remaining" value={remaining} color="bg-yellow-500" />
    </div>
  );
};

export default SummarySection;
