import React, { useEffect, useState } from 'react';
import axios from 'axios';
import TransactionTable from '../components/TransactionTable';
import { formatCurrency } from '../utils/formatter';
import jsPDF from 'jspdf';
import autoTable from 'jspdf-autotable';
jsPDF.API.autoTable = autoTable;
import * as XLSX from 'xlsx';
import { saveAs } from 'file-saver';

const Dashboard = () => {
  const [partyId, setPartyId] = useState('');
  const [factoryId, setFactoryId] = useState('');
  const [partyOptions, setPartyOptions] = useState([]);
  const [factoryOptions, setFactoryOptions] = useState([]);
  const [partyData, setPartyData] = useState(null);
  const [factoryData, setFactoryData] = useState(null);

  useEffect(() => {
    const fetchDropdowns = async () => {
      try {
        const [partiesRes, factoriesRes] = await Promise.all([
          axios.get('http://localhost:5001/api/parties'),
          axios.get('http://localhost:5001/api/factories')
        ]);
        setPartyOptions(partiesRes.data);
        setFactoryOptions(factoriesRes.data);
      } catch (err) {
        console.error('Failed to fetch party/factory lists', err);
      }
    };

    fetchDropdowns();
  }, []);

  useEffect(() => {
    if (partyId) {
      axios.get(`http://localhost:5001/api/dashboard/party/${partyId}`)
        .then(res => setPartyData(res.data))
        .catch(err => console.error(err));
    } else {
      setPartyData(null);
    }
    if (factoryId) {
      axios.get(`http://localhost:5001/api/dashboard/factory/${factoryId}`)
        .then(res => setFactoryData(res.data))
        .catch(err => console.error(err));
    } else {
      setFactoryData(null);
    }
  }, [partyId, factoryId]);

  // PDF Export function for party or factory summary
  const exportSummaryAsPDF = (type) => {
    const data = type === 'party' ? partyData : factoryData;
    if (!data) return;

    const doc = new jsPDF();
    doc.setFontSize(18);
    doc.text(`${type === 'party' ? 'Party' : 'Factory'} Summary`, 14, 22);

    doc.setFontSize(12);
    doc.text(`Total Weight: ${data.totalWeight} kg`, 14, 32);
    doc.text(`Total Amount: ₹ ${data.totalAmount}`, 14, 40);
    if (type === 'party') {
      doc.text(`Total Paid: ₹ ${data.totalPaid}`, 14, 48);
    } else {
      doc.text(`Total Received: ₹ ${data.totalReceived}`, 14, 48);
    }
    doc.text(`Remaining: ₹ ${data.remaining}`, 14, 56);

    // Transactions table
    const transactionsColumns = ['Date', 'Amount', 'Weight', 'Rate'];
    const transactionsRows = data.transactions.map(tx => [
      tx.date,
      `₹ ${tx.amount}`,
      tx.weight,
      tx.rate
    ]);

    doc.autoTable({
      startY: 65,
      head: [transactionsColumns],
      body: transactionsRows,
      styles: { fontSize: 10 }
    });

    // Save the PDF
    doc.save(`${type}_summary_${Date.now()}.pdf`);
  };

  // Excel export for party or factory summary
  const exportSummaryAsExcel = (type) => {
    const data = type === 'party' ? partyData : factoryData;
    if (!data) return;

    // Create worksheet data for summary
    const summarySheet = [
      ['Total Weight', data.totalWeight],
      ['Total Amount', data.totalAmount],
      [type === 'party' ? 'Total Paid' : 'Total Received', type === 'party' ? data.totalPaid : data.totalReceived],
      ['Remaining', data.remaining],
    ];

    // Transactions sheet data
    const transactionsSheet = [
      ['Date', 'Amount', 'Weight', 'Rate'],
      ...data.transactions.map(tx => [tx.date, tx.amount, tx.weight, tx.rate])
    ];

    // Create a new workbook
    const wb = XLSX.utils.book_new();

    // Add summary sheet
    const wsSummary = XLSX.utils.aoa_to_sheet(summarySheet);
    XLSX.utils.book_append_sheet(wb, wsSummary, 'Summary');

    // Add transactions sheet
    const wsTransactions = XLSX.utils.aoa_to_sheet(transactionsSheet);
    XLSX.utils.book_append_sheet(wb, wsTransactions, 'Transactions');

    // Write workbook and trigger download
    const wbout = XLSX.write(wb, { bookType: 'xlsx', type: 'array' });
    saveAs(new Blob([wbout], { type: 'application/octet-stream' }), `${type}_summary_${Date.now()}.xlsx`);
  };

  return (
    <div className="p-6">
      <h2 className="text-3xl font-bold mb-6">Dashboard</h2>

      <div className="grid grid-cols-1 md:grid-cols-2 gap-6 mb-8">
        <div>
          <label className="block font-semibold mb-1">Select Party:</label>
          <select
            value={partyId}
            onChange={(e) => setPartyId(e.target.value)}
            className="w-full border border-gray-300 p-2 rounded"
          >
            <option value="">-- Select Party --</option>
            {partyOptions.map((party) => (
              <option key={party.id} value={party.id}>
                {party.name}
              </option>
            ))}
          </select>
        </div>

        <div>
          <label className="block font-semibold mb-1">Select Factory:</label>
          <select
            value={factoryId}
            onChange={(e) => setFactoryId(e.target.value)}
            className="w-full border border-gray-300 p-2 rounded"
          >
            <option value="">-- Select Factory --</option>
            {factoryOptions.map((factory) => (
              <option key={factory.id} value={factory.id}>
                {factory.name}
              </option>
            ))}
          </select>
        </div>
      </div>

      {partyData && (
        <div className="bg-white p-5 shadow rounded mb-8">
          <div className="flex justify-between items-center mb-4">
            <h3 className="text-xl font-semibold">Party Summary</h3>
            <div className="flex gap-2">
              <button
                onClick={() => exportSummaryAsPDF('party')}
                className="bg-blue-600 text-white px-3 py-1 rounded hover:bg-blue-700"
              >
                Export PDF
              </button>
              <button
                onClick={() => exportSummaryAsExcel('party')}
                className="bg-green-600 text-white px-3 py-1 rounded hover:bg-green-700"
              >
                Export Excel
              </button>
            </div>
          </div>
          <p><strong>Total Weight:</strong> {partyData.totalWeight} kg</p>
          <p><strong>Total Amount:</strong> {formatCurrency(partyData.totalAmount)}</p>
          <p><strong>Total Paid:</strong> {formatCurrency(partyData.totalPaid)}</p>
          <p><strong>Remaining:</strong> {formatCurrency(partyData.remaining)}</p>
          <h4 className="mt-4 font-semibold">Transactions</h4>
          <TransactionTable transactions={partyData.transactions} />
        </div>
      )}

      {factoryData && (
        <div className="bg-white p-5 shadow rounded">
          <div className="flex justify-between items-center mb-4">
            <h3 className="text-xl font-semibold">Factory Summary</h3>
            <div className="flex gap-2">
              <button
                onClick={() => exportSummaryAsPDF('factory')}
                className="bg-blue-600 text-white px-3 py-1 rounded hover:bg-blue-700"
              >
                Export PDF
              </button>
              <button
                onClick={() => exportSummaryAsExcel('factory')}
                className="bg-green-600 text-white px-3 py-1 rounded hover:bg-green-700"
              >
                Export Excel
              </button>
            </div>
          </div>
          <p><strong>Total Weight:</strong> {factoryData.totalWeight} kg</p>
          <p><strong>Total Amount:</strong> {formatCurrency(factoryData.totalAmount)}</p>
          <p><strong>Total Received:</strong> {formatCurrency(factoryData.totalReceived)}</p>
          <p><strong>Remaining:</strong> {formatCurrency(factoryData.remaining)}</p>
          <h4 className="mt-4 font-semibold">Transactions</h4>
          <TransactionTable transactions={factoryData.transactions} />
        </div>
      )}
    </div>
  );
};

export default Dashboard;
