import React, { useState, useEffect } from "react";
import PartyForm from "../components/PartyForm";
import partyService from "../services/partyService";

const PartiesPage = () => {
  const [parties, setParties] = useState([]);

  const fetchParties = async () => {
    try {
      const data = await partyService.getAllParties();
      setParties(data);
    } catch (err) {
      console.error("Failed to fetch parties", err);
    }
  };

  useEffect(() => {
    fetchParties();
  }, []);

  return (
    <div className="p-6 min-h-screen bg-gray-50">
      <h1 className="text-3xl font-bold mb-6 text-green-600">Manage Parties</h1>

      <div className="bg-white rounded-2xl shadow-md p-6 mb-8 border border-gray-200">
        <h2 className="text-xl font-semibold mb-4 text-gray-700">Add New Party</h2>
        <PartyForm onAdded={fetchParties} />
      </div>

      <div className="bg-white rounded-2xl shadow-md border border-gray-200">
        <div className="overflow-x-auto">
          <table className="w-full table-auto border-collapse">
            <thead className="bg-green-100 text-green-800">
              <tr>
                <th className="px-4 py-3 text-left border-b">Name</th>
                <th className="px-4 py-3 text-left border-b">Contact</th>
                <th className="px-4 py-3 text-left border-b">Address</th>
                <th className="px-4 py-3 text-left border-b">GSTIN</th>
              </tr>
            </thead>
            <tbody>
              {parties.map((party, index) => (
                <tr
                  key={party.id}
                  className={`hover:bg-green-50 transition ${index % 2 === 0 ? "bg-white" : "bg-gray-50"}`}
                >
                  <td className="px-4 py-3 border-b">{party.name}</td>
                  <td className="px-4 py-3 border-b">{party.contact}</td>
                  <td className="px-4 py-3 border-b">{party.address}</td>
                  <td className="px-4 py-3 border-b">{party.gstin}</td>
                </tr>
              ))}
              {parties.length === 0 && (
                <tr>
                  <td colSpan="4" className="text-center py-6 text-gray-500">
                    No parties available. Add one above!
                  </td>
                </tr>
              )}
            </tbody>
          </table>
        </div>
      </div>
    </div>
  );
};

export default PartiesPage;
