import React, { useState, useEffect } from "react";
import PartyForm from "../components/PartyForm";
import partyService from "../services/partyService";

const PartiesPage = ({ filters }) => {
  const [parties, setParties] = useState([]);

  const fetchParties = async () => {
    try {
      const data = await partyService.getFilteredParties(filters);
      setParties(data);
    } catch (err) {
      console.error("Failed to fetch parties", err);
    }
  };

  useEffect(() => {
    fetchParties();
  }, []);

  return (
    <div className="p-6">
      <h1 className="text-2xl font-bold mb-4">Parties</h1>
      <PartyForm onAdded={fetchParties} />

      <table className="w-full mt-6 border border-gray-300 rounded overflow-hidden">
        <thead className="bg-gray-100">
          <tr>
            <th className="border border-gray-300 p-2 text-left">Name</th>
            <th className="border border-gray-300 p-2 text-left">Contact</th>
            <th className="border border-gray-300 p-2 text-left">Address</th>
            <th className="border border-gray-300 p-2 text-left">GSTIN</th>
          </tr>
        </thead>
        <tbody>
          {parties.map((party) => (
            <tr key={party.id} className="hover:bg-gray-50">
              <td className="border border-gray-300 p-2">{party.name}</td>
              <td className="border border-gray-300 p-2">{party.contact}</td>
              <td className="border border-gray-300 p-2">{party.address}</td>
              <td className="border border-gray-300 p-2">{party.gstin}</td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
};

export default PartiesPage;
